let box = document.getElementById("box");

let divs = [];

function beep() {
  let snd = new Audio(
    "data:audio/wav;base64,"
  );
  snd.play();
}

function draw(idx, arr) {
  box.innerHTML = null;
  for (let i = 0; i < 360; i++) {
    box.appendChild(arr[idx[i] - 1]);
  }
  beep();
}

function shuffle(array) {
  let currentIndex = array.length,
    randomIndex;
  while (currentIndex != 0) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;
    [array[currentIndex], array[randomIndex]] = [
      array[randomIndex],
      array[currentIndex],
    ];
  }
  return array;
}

let colors = [];

for (let i = 1; i < 361; i++) {
  colors.push(i);
  const div = document.createElement("div");
  div.style.width = "1.5px";
  div.style.height = "200px";
  div.style.backgroundColor = `hsl(${i}, 100%, 50%)`;
  div.style.transform = `rotate(${i}deg)`;
  divs.push(div);
}

colors = shuffle(colors);

draw(colors, divs);

function bblSort(arr) {
  for (let i = 0; i < arr.length; i++) {
    setTimeout(
      (k) => {
        for (let j = 0; j < arr.length - k - 1; j++) {
          if (arr[j] > arr[j + 1]) {
            let temp = arr[j];
            arr[j] = arr[j + 1];
            arr[j + 1] = temp;
          }
        }
        draw(arr, divs);
      },
      i * 50,
      i
    );
  }
}

function insertionSort(arr) {
  let i,
    _key,
    j,
    n = arr.length;
  for (i = 1; i < n; i++) {
    _key = arr[i];
    setTimeout(
      (k, key) => {
        while (k >= 0 && arr[k] > key) {
          arr[k + 1] = arr[k];
          k = k - 1;
        }
        arr[k + 1] = key;
        draw(arr, divs);
      },
      i * 50,
      i - 1,
      _key
    );
  }
}

function selectionSort(arr) {
  let i, j, min_idx;
  n = arr.length;
  for (i = 0; i < n - 1; i++) {
    min_idx = i;
    setTimeout(
      (k, mi) => {
        for (j = k + 1; j < n; j++) if (arr[j] < arr[mi]) mi = j;
        let t = arr[mi];
        arr[mi] = arr[k];
        arr[k] = t;
        draw(arr, divs);
      },
      i * 50,
      i,
      min_idx
    );
  }
}

function merge(arr, l, m, r) {
  let n1 = m - l + 1,
    n2 = r - m,
    L = new Array(n1),
    R = new Array(n2);
  for (let i = 0; i < n1; i++) L[i] = arr[l + i];
  for (let j = 0; j < n2; j++) R[j] = arr[m + 1 + j];
  let i = 0;
  let j = 0;
  let k = l;
  while (i < n1 && j < n2) {
    if (L[i] <= R[j]) {
      arr[k] = L[i];
      i++;
    } else {
      arr[k] = R[j];
      j++;
    }
    k++;
  }
  while (i < n1) {
    arr[k] = L[i];
    i++;
    k++;
  }
  while (j < n2) {
    arr[k] = R[j];
    j++;
    k++;
  }
}

let i = 0;
function mergeSort(arr, l, r) {
  if (l >= r) {
    return;
  }
  let m = l + parseInt((r - l) / 2);
  mergeSort(arr, l, m);
  mergeSort(arr, m + 1, r);
  setTimeout(
    (_l, _m, _r) => {
      merge(arr, _l, _m, _r);
      draw(arr, divs);
    },
    i * 50,
    l,
    m,
    r
  );
  i++;
}

function triggerbubble() {
  bblSort(colors);
}

function triggerinsertion() {
  insertionSort(colors);
}

function triggerSelection() {
  selectionSort(colors);
}

function triggerMerge() {
  mergeSort(colors, 0, colors.length - 1);
}
